defined('SECURE_ENTRY') or define('SECURE_ENTRY', true);

// Example hex-encoded content from the first encoder.html (replace with your actual encoded content)
$antibot = "";
$inspectElement = @hex2bin($antibot);
if ($inspectElement === false) {
    $inspectElement = "<p>Error decoding content.</p>";
} else {
    // Ensure the decoded content is treated as UTF-8
    $inspectElement = mb_convert_encoding($inspectElement, 'UTF-8', 'UTF-8');
    // Optional: Validate UTF-8 integrity
    if (!mb_check_encoding($inspectElement, 'UTF-8')) {
        $inspectElement = "<p>Decoded content is not valid UTF-8.</p>";
    }
}
